using namespace std;

#define numofchars 256

void calculateResult(int numOfHits, int patternLen, int textLen){
    float patdiv = (float)numOfHits/(float)patternLen;
    float txtdiv = (float)numOfHits/(float)textLen;
    double patshared = patdiv*100;
    double txtshared = txtdiv*100;
    double total = (patshared+txtshared)/2;
    cout<<total;
}

int search(char pat[], char txt[], int primeNumber){
    int patternLen = strlen(pat);
	int textLen = strlen(txt);
    int res=0;
    char* newpat;
    newpat = strtok(pat,".");
    int whilecounter=0;
    while(newpat!=NULL){
        int i,j;
        int patternHash = 0; // hash value for pattern
        int textHash = 0; // hash value for txt
        int h = 1;

        int senLen = strlen(newpat);
        for(i = 0; i < senLen-1; i++){
            h = (h*numofchars)%primeNumber;
        }
        //char newpat[]=sentence;
        //Hash First pattern and text (execute only once)
        for (i = 0; i < senLen; i++){
            patternHash = (numofchars*patternHash + newpat[i])%primeNumber;
            textHash = (numofchars*textHash + txt[i])%primeNumber;
        }
        cout<< "Pattern hash: "<<patternHash <<endl;
        cout<< "Text hash: "<<textHash <<endl;

        for (i = 0; i <= textLen - senLen; i++){
            if(patternHash==textHash){
                for (j = 0; j < senLen; j++){
                    if (txt[i+j] != newpat[j]){
                        break;
                    }
                }
                if (j == senLen){
                    res++;
                }
            }
            // Calculate hash value for next window of text: Remove
            // leading digit, add trailing digit
            if ( i < textLen-senLen ){
                textHash = (numofchars*(textHash - txt[i]*h) + txt[i+senLen])%primeNumber;
                // We might get negative value of t, converting it
                // to positive
                if (textHash < 0){
                    textHash = (textHash + primeNumber);
                }
                //cout<< "Text hash: "<<t <<endl;
            }
        }
        newpat = strtok(NULL, ".");
        whilecounter++;
    }
    calculateResult(res,patternLen,textLen);
    cout<<endl<<"While: "<<whilecounter<<endl;
    return res;
}

